apt_preference.body:
apt_preference '${1:name}' do
	pin '${2:version 1.2.3}'
	pin_priority ${3:700}
	action :${4:add}
end

apt_repository.body:
apt_repository '${1:name}' do
	uri '${2:https://example.com/deb}'
	components ${3:['main']}
	action :${4:add}
end

bash.body:
bash '${1:name}' do
	code <<-EOH
	${5:script commands}
	EOH
	action :${2:run}
end

batch.body:
batch '${1:name}' do
	code <<-EOH
	${5:batch commands}
	EOH
	action :${2:run}
end

csh.body:
csh '${1:name}' do
	code <<-EOH
	${5:script commands}
	EOH
	action :${6:run}
end

reboot.body:
reboot '${1:name}' do
	action :${2:request_reboot}
	reason '${3:reason}'
	delay_mins ${4:minutes}
end

registry_key.body:
registry_key '${1:key_name}' do
	values [{
		name: '${2:name}',
		type: '${3:type}',
		data: '${4:data}'
	}]
	action :${5:create}
end

ruby_block.body:
ruby_block '${1:name}' do
	block do
		${2:code}
	end
	action :${3:run}
end
